from collections import Counter

def count_elements(sequence):
    """
    Count the frequency of each element in the sequence
    using the Counter class from the collections module.
    """
    return Counter(sequence)

# Example usage:
if __name__ == "__main__":
    sequence_input = input("Enter elements separated by spaces: ").split()
    element_counts = count_elements(sequence_input)
    for element, count in element_counts.items():
        print(f"{element}: {count}")
