from collections import deque
from typing import Any, Callable


class DependencyInjector:
    def __init__(self):
        self._dependencies = {}

    def add_dependency(self, name: str, dependency: Any):
        self._dependencies[name] = dependency

    def get_dependency(self, name: str):
        return self._dependencies.get(name)

    def inject(self, func: Callable):
        def wrapper(*args, **kwargs):
            new_kwargs = {
                **kwargs,
                **{k: self.get_dependency(k) for k in self._dependencies},
            }
            return func(*args, **new_kwargs)

        return wrapper


class TaskQueue:
    def __init__(self):
        self._queue = deque()

    def add_task(self, task):
        self._queue.append(task)

    def run_tasks(self):
        while self._queue:
            task = self._queue.popleft()
            task.execute()


class Task:
    def __init__(self, name, action):
        self.name = name
        self.action = action

    def execute(self):
        print(f"Executing {self.name}")
        self.action()


# Example usage
if __name__ == "__main__":
    def my_custom_task():
        print("This is a custom task.")

    injector = DependencyInjector()
    injector.add_dependency("task_queue", TaskQueue())

    @injector.inject
    def add_task_to_queue(task_queue: TaskQueue, task_name: str, action: Callable):
        task = Task(task_name, action)
        task_queue.add_task(task)

    # Adding tasks to the queue
    add_task_to_queue(task_name="CustomTask1", action=my_custom_task)
    add_task_to_queue(task_name="CustomTask2", action=lambda: print("Another custom task."))

    # Getting the task queue and running tasks
    task_queue = injector.get_dependency("task_queue")
    task_queue.run_tasks()
