from collections import deque


def recent_items_tracker():
    recent_items = deque(maxlen=5)
    item_log = dict()

    while True:
        action = input("Enter 'add', 'get', 'exit': ").strip().lower()

        if action == 'add':
            item = input("Enter the item to add: ").strip()
            if item:
                recent_items.append(item)
                item_log[item] = item_log.get(item, 0) + 1
                print(f"Added item: {item}")
            else:
                print("No item entered. Please try again.")

        elif action == 'get':
            item = input("Enter the item to retrieve: ").strip()
            if item in item_log:
                print(f"The item '{item}' has been accessed {item_log[item]} times.")
            else:
                print(f"The item '{item}' has not been accessed before.")

        elif action == 'exit':
            print("Exiting the program.")
            break

        else:
            print("Invalid action. Please enter 'add', 'get', or 'exit'.")

        print(f"Recent items: {list(recent_items)}")


if __name__ == "__main__":
    recent_items_tracker()
