def find_unique_elements(dict_list):
    """
    Takes a list of dictionaries and returns a frozenset containing all unique elements
    across all dictionaries' values.
    """
    unique_elements = set()
    for dictionary in dict_list:
        for value in dictionary.values():
            if isinstance(value, list):
                unique_elements.update(value)
            else:
                unique_elements.add(value)
    return frozenset(unique_elements)


def main():
    list_of_dictionaries = [
        {"fruits": ["apple", "banana", "cherry"], "vegetables": ["carrot"]},
        {"fruits": ["apple", "kiwi"], "berries": ["blueberry", "cherry"]},
    ]

    unique_elements = find_unique_elements(list_of_dictionaries)
    print("Unique elements across all dictionaries:", unique_elements)


if __name__ == "__main__":
    main()
