def reverse_bytearray(input_bytes):
    """
    Takes a bytearray or bytes object and returns a new bytearray
    with the elements in the reverse order.
    """
    if not isinstance(input_bytes, (bytearray, bytes)):
        raise TypeError("Input must be a bytearray or bytes object")

    return bytearray(reversed(input_bytes))


def main():
    original_data = bytearray("This is a test string.", "utf-8")
    print("Original bytearray:", original_data)

    reversed_data = reverse_bytearray(original_data)
    print("Reversed bytearray:", reversed_data)


if __name__ == "__main__":
    main()
