def reverse_bytearray(byte_array):
    """
    Reverses the order of bytes in a bytearray and returns the new bytearray.
    """
    return bytearray(reversed(byte_array))


def main():
    data = bytearray("Hello, World!", "utf-8")
    print("Original bytearray:", data)
    reversed_data = reverse_bytearray(data)
    print("Reversed bytearray:", reversed_data)


if __name__ == "__main__":
    main()
