from collections import ChainMap


def merge_data(defaults, overrides):
    """
    Merge two dictionaries using a ChainMap.
    """
    return ChainMap(overrides, defaults)


def bytes_comprehension(size):
    """
    Generate a bytes object with values from 0 to 255 repeated until the given size is reached.
    """
    return bytes(byte for byte in range(256) for _ in range(size // 256 + 1))[:size]


def main():
    defaults = {"username": "user", "password": "pass123"}
    overrides = {"password": "securepass"}

    # Merge dictionaries using ChainMap
    merged_config = merge_data(defaults, overrides)
    print("Merged configuration:", merged_config.maps)

    # Generate a bytes object of a specified size
    size = 1024  # 1KB
    data = bytes_comprehension(size)
    print(f"Bytes object of size {size} created.")


if __name__ == "__main__":
    main()
