from collections import ChainMap


def merge_dictionaries(dict1, dict2):
    """
    Merge two dictionaries using ChainMap and return a unified dictionary.
    The values from dict1 will take precedence over values from dict2.
    """
    chain = ChainMap(dict1, dict2)
    return dict(chain)


def main():
    user_defaults = {"theme": "Light", "language": "English", "notifications": True}
    user_preferences = {"theme": "Dark", "language": "Spanish"}

    merged_preferences = merge_dictionaries(user_preferences, user_defaults)
    print("Merged User Preferences:")
    for key, value in merged_preferences.items():
        print(f"{key}: {value}")


if __name__ == "__main__":
    main()
