def calculate_squares(numbers):
    """
    This function takes a list of numbers and returns a dictionary
    where each number is a key and its square is the value.
    """
    return {number: number ** 2 for number in numbers}


def main():
    num_list = [1, 2, 3, 4, 5]
    squares_dict = calculate_squares(num_list)
    for num, square in squares_dict.items():
        print(f"The square of {num} is {square}")


if __name__ == "__main__":
    main()
