def generate_squares(n):
    """
    Generate a dictionary where the keys are numbers up to n
    and the values are the squares of the keys.
    """
    return {num: num**2 for num in range(1, n + 1)}


def main():
    num = int(input("Enter a number to generate squares up to: "))
    squares_dict = generate_squares(num)
    for key, value in squares_dict.items():
        print(f"{key} squared is {value}")


if __name__ == "__main__":
    main()
