def find_duplicates(input_list):
    """
    This function finds and returns a list of duplicate items from the given list.
    """
    duplicates = []
    seen = set()
    for item in input_list:
        if item in seen:
            duplicates.append(item)
        else:
            seen.add(item)
    return duplicates

def main():
    # Example list with some duplicates
    example_list = [1, 2, 3, 2, 4, 5, 6, 5, 7, 8, 7]
    duplicates = find_duplicates(example_list)

    print(f"Duplicate items in the list: {duplicates}")

if __name__ == "__main__":
    main()
