import sys

def bytes_to_human_readable(num_bytes):
    """
    Converts a number of bytes into a human-readable string with units.
    """
    for unit in ['B', 'KB', 'MB', 'GB', 'TB', 'PB', 'EB']:
        if num_bytes < 1024.0:
            return f"{num_bytes:3.1f}{unit}"
        num_bytes /= 1024.0
    return f"{num_bytes:.1f}ZB"

def main():
    if len(sys.argv) != 2:
        print("Usage: python bytes_converter.py <number_of_bytes>")
        sys.exit(1)

    try:
        num_bytes = int(sys.argv[1])
    except ValueError:
        print("Please enter a valid integer for the number of bytes.")
        sys.exit(1)

    human_readable = bytes_to_human_readable(num_bytes)
    print(f"{num_bytes} bytes is equivalent to {human_readable}")

if __name__ == "__main__":
    main()
