from collections import ChainMap

def merge_dictionaries(dict1, dict2):
    """
    This function takes two dictionaries and merges them using ChainMap. The values from the first
    dictionary have precedence over the values from the second dictionary in case of key collisions.
    """
    chain = ChainMap(dict1, dict2)
    return chain

def main():
    # Two sample dictionaries
    dict1 = {'a': 1, 'b': 2}
    dict2 = {'b': 3, 'c': 4}

    # Merge dictionaries using ChainMap
    merged_dict = merge_dictionaries(dict1, dict2)

    # Print merged dictionary
    for key, value in merged_dict.items():
        print(f"{key}: {value}")

if __name__ == "__main__":
    main()
