# Python program to generate a dictionary with list comprehension
# The dictionary will map each number from 1 to 10 with its square

def generate_squares_dict():
    return {num: num**2 for num in range(1, 11)}

if __name__ == "__main__":
    squares_dict = generate_squares_dict()
    for number, square in squares_dict.items():
        print(f"The square of {number} is {square}")
