from collections import deque

def manage_orders():
    """Manage orders using deque and dict for quick access and updates."""
    order_queue = deque()
    order_details = {}

    while True:
        action = input("Enter action (add, serve, exit): ").strip().lower()

        if action == 'add':
            order_id = input("Enter order ID: ").strip()
            details = input("Enter order details: ").strip()
            order_queue.append(order_id)
            order_details[order_id] = details
            print(f"Order {order_id} added.")

        elif action == 'serve':
            if order_queue:
                served_order = order_queue.popleft()
                served_details = order_details.pop(served_order)
                print(f"Order {served_order} served with details: {served_details}")
            else:
                print("No orders to serve.")

        elif action == 'exit':
            print("Exiting order manager.")
            break
        else:
            print("Invalid action. Please choose 'add', 'serve', or 'exit'.")

if __name__ == "__main__":
    manage_orders()
