def create_frozenset_of_dicts(*dict_args):
    """
    Create a frozenset containing the given dictionaries.

    Args:
        *dict_args: Variable length dictionary list.

    Returns:
        A frozenset containing the dictionaries.
    """
    return frozenset(dict_args)


def main():
    # Example dictionaries
    dict1 = {'apple': 1, 'banana': 2}
    dict2 = {'orange': 3, 'grapes': 4}
    dict3 = {'kiwi': 5, 'pineapple': 6}

    # Create a frozenset of dictionaries
    frozenset_dicts = create_frozenset_of_dicts(dict1, dict2, dict3)

    # Print the frozenset of dictionaries
    print("Frozenset of dictionaries:", frozenset_dicts)


if __name__ == "__main__":
    main()
