def convert_to_bytearray(input_string):
    """Convert a string to a bytearray."""
    if not isinstance(input_string, str):
        raise ValueError("Input must be a string.")

    return bytearray(input_string, 'utf-8')


def main():
    user_input = input("Enter a string to convert to bytearray: ")
    try:
        byte_array = convert_to_bytearray(user_input)
        print("Bytearray representation:", byte_array)
    except ValueError as e:
        print(e)


if __name__ == "__main__":
    main()
