from collections import ChainMap
import sys


def merge_dictionaries_with_comprehension(*dicts):
    """Merge multiple dictionaries using dictionary comprehension."""
    return {k: v for d in dicts for k, v in d.items()}


def convert_to_bytes(data):
    """Convert a dictionary's values to bytes."""
    return {k: bytes(v, 'utf-8') if isinstance(v, str) else v for k, v in data.items()}


def main():
    """Main function that uses ChainMap and comprehension to solve a problem."""
    # Create two dictionaries
    dict1 = {'apple': 1, 'banana': 2}
    dict2 = {'berry': 3, 'orange': 4}

    # Merge dictionaries using ChainMap
    merged_dict = ChainMap(dict1, dict2)

    # Use comprehension to merge dictionaries into a new dictionary
    merged_dict_comprehension = merge_dictionaries_with_comprehension(dict1, dict2)

    # Convert the merged dictionary values to bytes
    bytes_dict = convert_to_bytes(merged_dict_comprehension)

    print(bytes_dict)


if __name__ == "__main__":
    main()
