from collections import ChainMap


def bytes_to_human_readable(num_bytes):
    """Convert bytes to a human readable format."""
    suffixes = ['B', 'KB', 'MB', 'GB', 'TB', 'PB']
    i = 0
    while num_bytes >= 1024 and i < len(suffixes) - 1:
        num_bytes /= 1024.
        i += 1
    return f"{num_bytes:.2f} {suffixes[i]}"


def merge_dictionaries(*dict_args):
    """Merge multiple dictionaries using ChainMap."""
    return ChainMap(*dict_args)


def square_numbers_comprehension(numbers):
    """Return a list of squares of the given numbers using list comprehension."""
    return [number ** 2 for number in numbers]


# Example usage:
if __name__ == "__main__":
    # Bytes to human readable
    print(bytes_to_human_readable(1024))
    print(bytes_to_human_readable(123456789))

    # Merge dictionaries using ChainMap
    dict1 = {'a': 1, 'b': 2}
    dict2 = {'b': 3, 'c': 4}
    merged_dict = merge_dictionaries(dict1, dict2)
    for key, value in merged_dict.items():
        print(f"{key}: {value}")

    # List comprehension to square numbers
    numbers = [1, 2, 3, 4, 5]
    print(square_numbers_comprehension(numbers))
