import concurrent.futures
from collections import ChainMap


def generate_squares(n):
    """Generate squares of numbers up to n using list comprehension."""
    return [i ** 2 for i in range(n)]


def merge_dictionaries(*dicts):
    """Merge dictionaries using ChainMap."""
    return dict(ChainMap(*dicts))


def concurrent_task(numbers):
    """Concurrently find the max number in lists."""
    with concurrent.futures.ThreadPoolExecutor() as executor:
        futures = [executor.submit(find_max, nums) for nums in numbers]
        return [future.result() for future in futures]


def find_max(numbers):
    """Find the maximum number in a list."""
    return max(numbers) if numbers else None


if __name__ == "__main__":
    squares = generate_squares(10)
    print(f"Squares up to 10: {squares}")

    dict1 = {'a': 1, 'b': 2}
    dict2 = {'b': 3, 'c': 4}
    merged_dict = merge_dictionaries(dict1, dict2)
    print(f"Merged Dictionary: {merged_dict}")

    list_of_numbers = [[3, 5, 1], [8, 7, 2], [9, 6, 0]]
    max_numbers = concurrent_task(list_of_numbers)
    print(f"Maximum numbers: {max_numbers}")
