import concurrent.futures


def compute_factorial(number):
    """Compute factorial of a given number."""
    if number == 0:
        return 1
    else:
        return number * compute_factorial(number - 1)


def main():
    numbers = [5, 7, 10]
    factorials = {}

    # Using concurrency to calculate factorial of numbers
    with concurrent.futures.ThreadPoolExecutor() as executor:
        futures = {executor.submit(compute_factorial, num): num for num in numbers}
        for future in concurrent.futures.as_completed(futures):
            num = futures[future]
            factorials[num] = future.result()

    for num, fac in factorials.items():
        print(f"The factorial of {num} is {fac}")


if __name__ == "__main__":
    main()
