from collections import ChainMap

def convert_to_bytes(data):
    """Convert a list of strings to a list of bytes."""
    return [s.encode() for s in data]

def merge_dictionaries(*dicts):
    """Merge multiple dictionaries using ChainMap."""
    return ChainMap(*dicts)

def main():
    # Example usage of convert_to_bytes with list comprehension
    strings = ['hello', 'world', 'python', 'coding']
    bytes_list = convert_to_bytes(strings)
    print("List of bytes:", bytes_list)

    # Example usage of ChainMap to merge dictionaries
    dict1 = {'a': 1, 'b': 2}
    dict2 = {'b': 3, 'c': 4}
    dict3 = {'d': 5}
    merged_dict = merge_dictionaries(dict1, dict2, dict3)
    print("Merged dictionary using ChainMap:", merged_dict)

if __name__ == "__main__":
    main()
