from collections import Counter, defaultdict

def count_decorator(func):
    def wrapper(*args, **kwargs):
        wrapper.calls += 1
        return func(*args, **kwargs)
    wrapper.calls = 0
    return wrapper

@count_decorator
def count_elements(iterable):
    """Count elements in the iterable and return a Counter object."""
    return Counter(iterable)

def group_by_first_letter(strings):
    """Group strings by their first letter using a defaultdict."""
    grouped = defaultdict(list)
    for string in strings:
        first_letter = string[0].lower()
        grouped[first_letter].append(string)
    return grouped

def main():
    # Counting elements example
    fruits = ['apple', 'banana', 'cherry', 'date', 'banana', 'cherry', 'cherry']
    fruit_counts = count_elements(fruits)
    print("Fruit counts:", fruit_counts)
    print("Function 'count_elements' was called", count_elements.calls, "times")

    # Grouping strings by first letter example
    words = ['apple', 'ant', 'banana', 'bat', 'cat', 'car']
    grouped_words = group_by_first_letter(words)
    for letter, words in grouped_words.items():
        print(f"Words starting with '{letter}': {words}")

if __name__ == "__main__":
    main()
