from collections import ChainMap

def count_byte_values(byte_string):
    """Count occurrences of each byte value in the byte string."""
    return {byte: byte_string.count(byte) for byte in set(byte_string)}

def merge_byte_counts(*byte_strings):
    """Merge byte counts from multiple byte strings using ChainMap."""
    byte_counts = [count_byte_values(byte_string) for byte_string in byte_strings]
    return dict(ChainMap(*byte_counts))

def main():
    byte_strings = [
        bytes([1, 2, 3, 4, 5]),
        bytes([2, 3, 4, 5, 6]),
        bytes([3, 4, 5, 6, 7])
    ]
    merged_counts = merge_byte_counts(*byte_strings)
    for byte_value, count in merged_counts.items():
        print(f"Byte value {byte_value}: {count}")

if __name__ == "__main__":
    main()
