from collections import ChainMap

def convert_to_bytes(data):
    """Converts a list of values to a list of bytes."""
    return [bytes(str(item), 'utf-8') for item in data]

def merge_dictionaries(*dicts):
    """Merge dictionaries using ChainMap and return a single dictionary."""
    chain = ChainMap(*dicts)
    return dict(chain)

def main():
    data = [123, 456, 789]
    byte_values = convert_to_bytes(data)
    print("Byte values:", byte_values)

    dict1 = {'a': 1, 'b': 2}
    dict2 = {'b': 3, 'c': 4}
    merged_dict = merge_dictionaries(dict1, dict2)
    print("Merged Dictionary:", merged_dict)

if __name__ == "__main__":
    main()
