from collections import ChainMap

def count_bytes_in_file(file_path):
    """Count the number of bytes for each type of byte in a file."""
    try:
        with open(file_path, 'rb') as file:
            byte_content = file.read()
    except FileNotFoundError:
        return "File not found."

    byte_count = {byte: byte_content.count(byte) for byte in set(byte_content)}
    return byte_count

def merge_byte_counts(*file_paths):
    """Merge byte counts from multiple files using a ChainMap."""
    all_counts = [count_bytes_in_file(file_path) for file_path in file_paths]
    return ChainMap(*all_counts)

def main():
    file_paths = ['file1.bin', 'file2.bin']
    merged_counts = merge_byte_counts(*file_paths)
    for byte, count in merged_counts.items():
        print(f'Byte {byte}: {count}')

if __name__ == "__main__":
    main()
