from collections import ChainMap
import concurrent.futures

def merge_dictionaries(dicts):
    """Merge multiple dictionaries using ChainMap."""
    return dict(ChainMap(*dicts))

def squares_comprehension(n):
    """Return a dictionary with numbers and their squares using comprehension."""
    return {i: i**2 for i in range(1, n + 1)}

def calculate_squares_concurrently(numbers):
    """Calculate squares of a list of numbers concurrently."""
    with concurrent.futures.ThreadPoolExecutor() as executor:
        future_to_num = {executor.submit(lambda x: x**2, num): num for num in numbers}
        return {future.result() for future in concurrent.futures.as_completed(future_to_num)}

def main():
    # Demonstrate ChainMap by merging dictionaries
    dict1 = {'a': 1, 'b': 2}
    dict2 = {'b': 3, 'c': 4}
    merged_dict = merge_dictionaries([dict1, dict2])
    print("Merged Dictionary:", merged_dict)

    # Demonstrate comprehension by creating a dictionary of squares
    squares_dict = squares_comprehension(5)
    print("Squares Dictionary:", squares_dict)

    # Demonstrate concurrency by calculating squares concurrently
    numbers = [1, 2, 3, 4, 5]
    squares = calculate_squares_concurrently(numbers)
    print("Concurrent Squares Calculation:", squares)

if __name__ == "__main__":
    main()
