from concurrent.futures import ThreadPoolExecutor

def is_prime(number):
    """Check if a number is a prime number."""
    if number < 2:
        return False
    for i in range(2, int(number ** 0.5) + 1):
        if number % i == 0:
            return False
    return True

def prime_numbers_in_range(start, end):
    """Return a list of prime numbers in a given range using list comprehension."""
    with ThreadPoolExecutor() as executor:
        futures = [executor.submit(is_prime, i) for i in range(start, end)]
        prime_numbers = [i for i, future in enumerate(futures, start=start) if future.result()]
    return prime_numbers

def main():
    start, end = 1, 100
    primes = prime_numbers_in_range(start, end)
    for prime in primes:
        print(prime)

if __name__ == "__main__":
    main()
