from collections import Counter
import asyncio

def coroutine_decorator(func):
    def wrapper(*args, **kwargs):
        cr = func(*args, **kwargs)
        next(cr)
        return cr
    return wrapper

@coroutine_decorator
def word_counter(counter):
    try:
        while True:
            word = (yield)
            counter[word] += 1
    except GeneratorExit:
        print("Word counts:", counter)

async def process_words(words, counter_cr):
    for word in words:
        counter_cr.send(word)
    counter_cr.close()

def main():
    counter_cr = word_counter(Counter())
    words = ["apple", "banana", "apple", "orange", "banana", "apple"]

    loop = asyncio.get_event_loop()
    loop.run_until_complete(process_words(words, counter_cr))
    loop.close()

if __name__ == "__main__":
    main()
