from collections import defaultdict

# Define a decorator to print the result of a function
def print_result(func):
    def wrapper(*args, **kwargs):
        result = func(*args, **kwargs)
        print(result)
        return result
    return wrapper

# Use defaultdict to count the occurrences of each item in a list
@print_result
def count_occurrences(lst):
    count_dict = defaultdict(int)
    for item in lst:
        count_dict[item] += 1
    return count_dict

# Example usage
if __name__ == "__main__":
    example_list = ["apple", "banana", "apple", "orange", "banana", "apple"]
    count_occurrences(example_list)
