from collections import defaultdict
from functools import wraps


# Decorator to count the occurrences of numbers
def count_occurrences(func):
    @wraps(func)
    def wrapper_count_occurrences(numbers):
        counter = defaultdict(int)
        for number in numbers:
            counter[number] += 1
        wrapper_count_occurrences.counter = counter
        return func(numbers)
    return wrapper_count_occurrences


@count_occurrences
def find_max(numbers):
    """Find the maximum number in a list."""
    return max(numbers) if numbers else None


# Example usage
if __name__ == "__main__":
    nums = [1, 3, 2, 3, 5, 3, 1]
    max_num = find_max(nums)
    print(f"The maximum number in the list is: {max_num}")
    print("Number occurrences:")
    for num, count in find_max.counter.items():
        print(f"{num}: {count}")
