def bytes_to_string(byte_list):
    """Convert a list of bytes to a string."""
    # Ensure that all elements in the list are bytes
    if not all(isinstance(item, bytes) for item in byte_list):
        raise ValueError("All items in the list must be bytes.")

    # Concatenate bytes in the list and decode to a string
    return b''.join(byte_list).decode()


# Example usage:
if __name__ == "__main__":
    byte_list_example = [b'Python', b' ', b'is', b' ', b'fun!']
    try:
        result = bytes_to_string(byte_list_example)
        print(result)
    except ValueError as e:
        print(f"Error: {e}")
