from collections import ChainMap


def merge_dicts(*dict_args):
    """Merge dictionaries using ChainMap and return a single dictionary."""
    chain = ChainMap(*dict_args)
    return dict(chain)


# Example usage
if __name__ == "__main__":
    dict1 = {'a': 1, 'b': 2}
    dict2 = {'b': 3, 'c': 4}
    dict3 = {'d': 5, 'e': 6}

    # Merge dictionaries
    merged_dict = merge_dicts(dict1, dict2, dict3)

    # Print the merged dictionary
    print(merged_dict)
