from collections import defaultdict

# Decorator to memoize the Fibonacci function
def memoize(f):
    memo = {}
    def helper(x):
        if x not in memo:
            memo[x] = f(x)
        return memo[x]
    return helper

@memoize
def fibonacci(n):
    """Return the nth Fibonacci number using memoization."""
    if n <= 1:
        return n
    return fibonacci(n - 1) + fibonacci(n - 2)

# Default dictionary to store Fibonacci numbers
fib_dict = defaultdict(int)

def populate_fibonacci_sequence(n, fib_dict):
    """Populate the fib_dict with Fibonacci numbers up to the nth number."""
    for i in range(n + 1):
        fib_dict[i] = fibonacci(i)

def main():
    # Populate the Fibonacci sequence up to the 9th number
    populate_fibonacci_sequence(9, fib_dict)

    # Print the Fibonacci sequence
    for i in range(10):
        print(fib_dict[i])

if __name__ == "__main__":
    main()
