from collections import ChainMap

def collect_chainmap_data(list_of_dicts):
    """
    Combine a list of dictionaries into a single ChainMap.

    The ChainMap groups multiple dictionaries into a single view.
    If there are duplicate keys, the values from the first dictionary
    in the list will be returned.
    """
    return ChainMap(*list_of_dicts)

def main():
    # List of dictionaries to be combined using ChainMap
    dict_list = [
        {'apple': 1, 'banana': 2},
        {'orange': 3, 'apple': 4},
        {'grapes': 5, 'banana': 6}
    ]

    # Create a ChainMap from the list of dictionaries
    combined_map = collect_chainmap_data(dict_list)

    # Access the values from the ChainMap
    for key in combined_map:
        print(f'{key}: {combined_map[key]}')

if __name__ == "__main__":
    main()
